package com.wcyc.zigui2.newapp.bean;import java.util.ArrayList;import java.util.List;public class Category {    private String mCategoryName;    private List<RecommendNewsBean.CampuEcirclesListBean> mCategoryItem = new ArrayList<>();    public Category(String mCategroyName) {        mCategoryName = mCategroyName;    }    public String getmCategoryName() {        return mCategoryName;    }    public void addItem(RecommendNewsBean.CampuEcirclesListBean bean) {        mCategoryItem.add(bean);    }    public Object getItem(int pPosition) {        if (pPosition == 0) {            return mCategoryName;        } else {            return mCategoryItem.get(pPosition - 1);        }    }    public int getItemCount() {        return mCategoryItem.size() + 1;    }}